		@Name
	)

END

CREATE PROCEDURE [dbo].[sp_InsertMedicalTestType]
(
	@Name nvarchar(50)
)
AS

BEGIN

	INSERT INTO Medication
	(
		Name,
		MedicationFormID
	)
	VALUES
	(
		@Name,
		@MedicationFormID
	)

END

CREATE PROCEDURE [dbo].[sp_InsertMedication]
(
	@Name nvarchar(50),
	@MedicationFormID int
)
AS

BEGIN

	INSERT INTO MedicationFlow
	(
		MedicationFlowTypeID,
		MedicationID,
		[Count],
		[Date],
		Note
	)
	VALUES
	(
		@MedicationFlowTypeID,
		@MedicationID,
		@Count,
		@Date,
		@Note
	)

	exec dbo.sp_UpdateMedicationCountInStock @MedicationID

END

CREATE PROCEDURE [dbo].[sp_InsertMedicationFlow]
(
	@MedicationFlowTypeID int,
	@MedicationID int,
	@Count int,
	@Date smalldatetime,
	@Note nvarchar(50)
)
AS

BEGIN

	INSERT INTO MedicationForm
	(
		Name
	)
	VALUES
	(
		@Name
	)

END

