CREATE PROCEDURE [dbo].[sp_InsertPatient]
(
	@FirstName nvarchar(50),
	@LastName nvarchar(50),
	@MiddleName nvarchar(50),
	@Birthday smalldatetime,
	@Notes nvarchar(max),
	@Education nvarchar(50),
	@WorkPosition nvarchar(50),
	@Phone nvarchar(50),
	@FinancialNotes nvarchar(max)
)
AS

BEGIN

	DECLARE @VisitID int

	INSERT INTO Visit
	(
		[PatientID]
       ,[Date]
       ,[Anamnesis]
       ,[Prescription]
	)
	VALUES
	(
		@PatientID,
		@Date,
		@Anamnesis,
		@Prescription
	)

	SET @VisitID = (SELECT @@Identity)
	RETURN ISNULL(@VisitID, 0)

END

